mapper.select:
select AUTO_ID, ts.WORK_ID as workId, OPERATE_USERID, to_char(OPERATE_DATE, 'yyyy-MM-dd hh24:mi:ss') OPERATE_DATE, IP, MODULE_NAME, FUNCTION_NAME, OPERATE_CONTENT from t_operate_log t inner join t_sys_user ts on t.OPERATE_USERID=ts.USER_ID where 1=1
and OPERATE_USERID=(select USER_ID from t_sys_user where WORK_ID=#{workId,jdbcType=VARCHAR})
and to_char(OPERATE_DATE, 'yyyy-MM-dd hh24:mi:ss') >= #{startoperateDate,jdbcType=VARCHAR}
and to_char(OPERATE_DATE, 'yyyy-MM-dd hh24:mi:ss') <= #{stopoperateDate,jdbcType=VARCHAR}
and t.ZUHU_ID=#{zuhuid,jdbcType=VARCHAR}
order by OPERATE_DATE desc

select count(1) from t_operate_log t inner join t_sys_user ts on t.OPERATE_USERID=ts.USER_ID where 1=1
and OPERATE_USERID=(select USER_ID from t_sys_user where WORK_ID=#{workId,jdbcType=VARCHAR})
and to_char(OPERATE_DATE, 'yyyy-MM-dd hh24:mi:ss') >= #{startoperateDate,jdbcType=VARCHAR}
and to_char(OPERATE_DATE, 'yyyy-MM-dd hh24:mi:ss') <= #{stopoperateDate,jdbcType=VARCHAR}
and t.ZUHU_ID=#{zuhuid,jdbcType=VARCHAR}
