mapper.select:
select AUTO_ID, ts.WORK_ID as workId, OPERATE_USERID, OPERATE_USERNAME, date_format(OPERATE_DATE,'%Y-%m-%d %H:%i:%s') OPERATE_DATE, IP, MODULE_NAME, FUNCTION_NAME, METHOD_NAME, CLASS_NAME, OPERATE_CONTENT, REMARK from t_operate_log t inner join t_sys_user ts on t.OPERATE_USERID=ts.USER_ID where 1=1
and OPERATE_USERID=(select USER_ID from t_sys_user where WORK_ID=#{workId,jdbcType=VARCHAR})
and OPERATE_DATE >= #{startoperateDate,jdbcType=VARCHAR}
and OPERATE_DATE <= #{stopoperateDate,jdbcType=VARCHAR}
and code=#{code}
order by AUTO_ID desc

select count(1) from t_operate_log t inner join t_sys_user ts on t.OPERATE_USERID=ts.USER_ID where 1=1
and OPERATE_USERID=(select USER_ID from t_sys_user where WORK_ID=#{workId,jdbcType=VARCHAR})
and OPERATE_USERID=(select USER_ID from t_sys_user where WORK_ID=#{workId,jdbcType=VARCHAR})
and OPERATE_DATE >= #{startoperateDate,jdbcType=VARCHAR}
and code=#{code}

select PK_AUTO_ID as AUTO_ID, ts.WORK_ID as workId, OPERATE_USERID, OPERATE_USERNAME, date_format(OPERATE_DATE,'%Y-%m-%d %H:%i:%s') OPERATE_DATE, IP, MODULE_NAME, FUNCTION_NAME, METHOD_NAME, CLASS_NAME, OPERATE_CONTENT, REMARK from sh_kd_ks_test.v_operate_log t inner join t_sys_user ts on t.OPERATE_USERID=ts.USER_ID where 1=1
and OPERATE_USERID=(select USER_ID from t_sys_user where WORK_ID=#{workId,jdbcType=VARCHAR})
and OPERATE_DATE >= #{startoperateDate,jdbcType=VARCHAR}
and OPERATE_DATE <= #{stopoperateDate,jdbcType=VARCHAR}
order by AUTO_ID desc
